package com.fxanhkhoa.what_to_eat_android.screens

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.lazy.rememberLazyListState
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Clear
import androidx.compose.material.icons.filled.Search
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.lifecycle.compose.collectAsStateWithLifecycle
import androidx.lifecycle.viewmodel.compose.viewModel
import com.fxanhkhoa.what_to_eat_android.data.model.QueryDishDto
import com.fxanhkhoa.what_to_eat_android.model.DishModel
import com.fxanhkhoa.what_to_eat_android.viewmodel.DishListViewModel
import com.fxanhkhoa.what_to_eat_android.viewmodel.FilterChipData

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun DishListScreen(
    modifier: Modifier = Modifier,
    viewModel: DishListViewModel = viewModel()
) {
    val dishes by viewModel.dishes.collectAsStateWithLifecycle()
    val isLoading by viewModel.isLoading.collectAsStateWithLifecycle()
    val hasMorePages by viewModel.hasMorePages.collectAsStateWithLifecycle()
    val errorMessage by viewModel.errorMessage.collectAsStateWithLifecycle()
    val hasActiveFilters by remember { derivedStateOf { viewModel.hasActiveFilters } }
    val activeFilterChips by remember { derivedStateOf { viewModel.activeFilterChips } }

    var searchText by remember { mutableStateOf("") }
    val listState = rememberLazyListState()

    // Load dishes on first composition
    LaunchedEffect(Unit) {
        viewModel.loadDishes(QueryDishDto())
    }

    // Infinite scroll effect
    LaunchedEffect(listState) {
        snapshotFlow { listState.layoutInfo.visibleItemsInfo.lastOrNull()?.index }
            .collect { lastVisibleIndex ->
                if (lastVisibleIndex != null && 
                    lastVisibleIndex >= dishes.size - 3 && 
                    hasMorePages && 
                    !isLoading) {
                    viewModel.loadMoreDishes()
                }
            }
    }

    Column(modifier = modifier.fillMaxSize()) {
        // Search bar
        OutlinedTextField(
            value = searchText,
            onValueChange = { 
                searchText = it
                viewModel.updateSearchKeyword(it)
            },
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            placeholder = { Text("Search dishes...") },
            leadingIcon = { Icon(Icons.Default.Search, contentDescription = "Search") },
            trailingIcon = {
                if (searchText.isNotEmpty()) {
                    IconButton(
                        onClick = { 
                            searchText = ""
                            viewModel.updateSearchKeyword("")
                        }
                    ) {
                        Icon(Icons.Default.Clear, contentDescription = "Clear search")
                    }
                }
            },
            singleLine = true
        )

        // Filter chips
        if (hasActiveFilters) {
            LazyRow(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(horizontal = 16.dp),
                horizontalArrangement = Arrangement.spacedBy(8.dp)
            ) {
                items(activeFilterChips) { chip ->
                    FilterChip(chip = chip)
                }

                item {
                    AssistChip(
                        onClick = { viewModel.clearAllFilters() },
                        label = { Text("Clear all") }
                    )
                }
            }
            Spacer(modifier = Modifier.height(8.dp))
        }

        // Error message
        errorMessage?.let { error ->
            Card(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                colors = CardDefaults.cardColors(containerColor = MaterialTheme.colorScheme.errorContainer)
            ) {
                Text(
                    text = error,
                    modifier = Modifier.padding(16.dp),
                    color = MaterialTheme.colorScheme.onErrorContainer
                )
            }
        }

        // Dishes list
        LazyColumn(
            state = listState,
            modifier = Modifier.fillMaxSize(),
            contentPadding = PaddingValues(16.dp),
            verticalArrangement = Arrangement.spacedBy(8.dp)
        ) {
            items(dishes) { dish ->
                DishListItem(dish = dish)
            }

            // Loading indicator
            if (isLoading) {
                item {
                    Box(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(16.dp),
                        contentAlignment = Alignment.Center
                    ) {
                        CircularProgressIndicator()
                    }
                }
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun FilterChip(
    chip: FilterChipData,
    modifier: Modifier = Modifier
) {
    AssistChip(
        onClick = { chip.onRemove() },
        label = { Text(chip.title) },
        trailingIcon = {
            Icon(
                Icons.Default.Clear,
                contentDescription = "Remove filter",
                modifier = Modifier.size(16.dp)
            )
        },
        modifier = modifier
    )
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun DishListItem(
    dish: DishModel,
    modifier: Modifier = Modifier
) {
    Card(
        modifier = modifier.fillMaxWidth(),
        onClick = { /* Handle dish item click */ }
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            Text(
                text = dish.getTitle("en") ?: dish.slug,
                style = MaterialTheme.typography.titleMedium,
                fontWeight = FontWeight.Bold
            )

            dish.getShortDescription("en")?.let { description ->
                Text(
                    text = description,
                    style = MaterialTheme.typography.bodyMedium,
                    modifier = Modifier.padding(top = 4.dp)
                )
            }

            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(top = 8.dp),
                horizontalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                dish.preparationTime?.let { time ->
                    Text(
                        text = "Prep: ${time}min",
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.outline
                    )
                }

                dish.cookingTime?.let { time ->
                    Text(
                        text = "Cook: ${time}min",
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.outline
                    )
                }

                dish.difficultLevel?.let { level ->
                    Text(
                        text = level.replaceFirstChar { it.uppercase() },
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.outline
                    )
                }
            }

            if (dish.tags.isNotEmpty()) {
                LazyRow(
                    modifier = Modifier.padding(top = 8.dp),
                    horizontalArrangement = Arrangement.spacedBy(4.dp)
                ) {
                    items(dish.tags.take(3)) { tag ->
                        SuggestionChip(
                            onClick = { /* Handle tag click */ },
                            label = { 
                                Text(
                                    text = "#$tag",
                                    style = MaterialTheme.typography.labelSmall
                                )
                            }
                        )
                    }

                    if (dish.tags.size > 3) {
                        item {
                            Text(
                                text = "+${dish.tags.size - 3} more",
                                style = MaterialTheme.typography.labelSmall,
                                color = MaterialTheme.colorScheme.outline,
                                modifier = Modifier.padding(start = 8.dp)
                            )
                        }
                    }
                }
            }
        }
    }
}

