import androidx.compose.ui.res.painterResource
import com.fxanhkhoa.what_to_eat_android.model.DishVoteItem
import com.fxanhkhoa.what_to_eat_android.ui.localization.Language
import com.fxanhkhoa.what_to_eat_android.ui.localization.LocalizationManager
import androidx.compose.ui.platform.LocalContext
import androidx.compose.foundation.shape.RoundedCornerShape
import coil.compose.AsyncImage

@Composable
fun SelectedDishRow(
    item: DishVoteItem,
    onRemove: () -> Unit,
    language: Language = Language.ENGLISH
) {
    val context = LocalContext.current
    val localizationManager = LocalizationManager(context)

    Surface(
        modifier = Modifier.fillMaxWidth(),
        color = MaterialTheme.colorScheme.surfaceVariant,
        shape = RoundedCornerShape(10.dp)
    ) {
        Row(
            modifier = Modifier.padding(12.dp),
            horizontalArrangement = Arrangement.spacedBy(12.dp),
            verticalAlignment = Alignment.CenterVertically
        ) {
            AsyncImage(
                model = if (item.isCustom) item.slug else null,
                contentDescription = null,
                modifier = Modifier
                    .size(50.dp)
                    .clip(RoundedCornerShape(8.dp)),
                contentScale = ContentScale.Crop,
                error = painterResource(com.fxanhkhoa.what_to_eat_android.R.drawable.ic_launcher_foreground),
                placeholder = painterResource(com.fxanhkhoa.what_to_eat_android.R.drawable.ic_launcher_foreground)
            )
            Column(
                modifier = Modifier.weight(1f),
                verticalArrangement = Arrangement.spacedBy(4.dp)
            ) {
                Row(
                    horizontalArrangement = Arrangement.spacedBy(8.dp),
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Text(
                        text = if (item.isCustom) {
                            item.customTitle ?: localizationManager.getString(com.fxanhkhoa.what_to_eat_android.R.string.custom_dish, language)
                        } else {
                            item.slug
                        },
                        style = MaterialTheme.typography.bodyMedium,
                        fontWeight = FontWeight.Medium,
                        maxLines = 1
                    )
                    Surface(
                        color = if (item.isCustom) {
                            Color(0xFFFF9800).copy(alpha = 0.2f)
                        } else {
                            Color(0xFF4CAF50).copy(alpha = 0.2f)
                        },
                        shape = RoundedCornerShape(4.dp)
                    ) {
                        Text(
                            text = localizationManager.getString(
                                if (item.isCustom) com.fxanhkhoa.what_to_eat_android.R.string.custom_dish else com.fxanhkhoa.what_to_eat_android.R.string.from_database,
                                language
                            ),
                            modifier = Modifier.padding(horizontal = 6.dp, vertical = 2.dp),
                            style = MaterialTheme.typography.labelSmall,
                            color = if (item.isCustom) Color(0xFFFF9800) else Color(0xFF4CAF50)
                        )
                    }
                }
                if (item.isCustom && item.customTitle != item.slug) {
                    Text(
                        text = item.slug,
                        style = MaterialTheme.typography.bodySmall,
                        color = MaterialTheme.colorScheme.onSurfaceVariant,
                        maxLines = 1
                    )
                }
            }
            IconButton(
                onClick = onRemove,
                modifier = Modifier.size(32.dp)
            ) {
                Icon(
                    imageVector = Icons.Default.Clear,
                    contentDescription = "Remove",
                    tint = MaterialTheme.colorScheme.error
                )
            }
        }
    }
}

package com.fxanhkhoa.what_to_eat_android.screens.game.voting

import androidx.compose.foundation.layout.*
import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.ui.Modifier
import androidx.compose.ui.unit.dp
import androidx.compose.ui.Alignment
import androidx.compose.ui.text.font.FontWeight
import com.fxanhkhoa.what_to_eat_android.model.DishVoteItem
import com.fxanhkhoa.what_to_eat_android.ui.localization.Language
import com.fxanhkhoa.what_to_eat_android.ui.localization.LocalizationManager
import androidx.compose.ui.platform.LocalContext
import com.fxanhkhoa.what_to_eat_android.screens.game.voting.SelectedDishRow
import com.fxanhkhoa.what_to_eat_android.screens.game.voting.EmptyDishesView

@Composable
fun SelectedDishesSection(
    dishes: List<DishVoteItem>,
    onRemoveDish: (Int) -> Unit,
    language: Language = Language.ENGLISH
) {
    val context = LocalContext.current
    val localizationManager = LocalizationManager(context)

    Card(
        modifier = Modifier.fillMaxWidth(),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        ),
        shape = RoundedCornerShape(12.dp)
    ) {
        Column(
            modifier = Modifier.padding(16.dp),
            verticalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = localizationManager.getString(com.fxanhkhoa.what_to_eat_android.R.string.selected_dishes, language),
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.SemiBold
                )
                Surface(
                    color = MaterialTheme.colorScheme.primary.copy(alpha = 0.2f),
                    shape = RoundedCornerShape(8.dp)
                ) {
                    Text(
                        text = "${dishes.size}",
                        modifier = Modifier.padding(horizontal = 8.dp, vertical = 4.dp),
                        style = MaterialTheme.typography.labelSmall,
                        color = MaterialTheme.colorScheme.primary
                    )
                }
            }
            if (dishes.isEmpty()) {
                EmptyDishesView(language = language)
            } else {
                Column(verticalArrangement = Arrangement.spacedBy(8.dp)) {
                    dishes.forEachIndexed { index, item ->
                        SelectedDishRow(
                            item = item,
                            onRemove = { onRemoveDish(index) },
                            language = language
                        )
                    }
                }
            }
        }
    }
}

